import React, { useState } from 'react'
import { Sidebar } from './components/Sidebar'
import { ASTExplorer } from './components/ASTExplorer'
import { TimelineView } from './components/TimelineView'
import { NetworkView } from './components/NetworkView'
import { MicrositePreview } from './components/MicrositePreview'
import { ThemeToggle } from './components/ThemeToggle'
export function App() {
  const [isPreviewOpen, setIsPreviewOpen] = useState(false)
  const [previewContent, setPreviewContent] = useState<{
    title: string
    content: React.ReactNode
  }>({
    title: '',
    content: null,
  })
  const [currentView, setCurrentView] = useState<
    'ast' | 'timeline' | 'network'
  >('ast')
  const openPreview = (title: string, content: React.ReactNode) => {
    setPreviewContent({
      title,
      content,
    })
    setIsPreviewOpen(true)
  }
  return (
    <div className="flex w-full min-h-screen bg-gray-950 text-gray-100">
      <Sidebar currentView={currentView} onViewChange={setCurrentView} />
      <div className="flex-1 flex flex-col">
        <header className="flex justify-between items-center p-4 border-b border-gray-800">
          <h1 className="text-xl font-semibold">
            <span className="text-purple-400">✨</span> FLOAT Conversation
            Explorer
          </h1>
          <ThemeToggle />
        </header>
        <main className="flex-1 overflow-auto">
          {currentView === 'ast' && (
            <ASTExplorer onPreviewMicrosite={openPreview} />
          )}
          {currentView === 'timeline' && (
            <TimelineView onPreviewMicrosite={openPreview} />
          )}
          {currentView === 'network' && (
            <NetworkView onPreviewMicrosite={openPreview} />
          )}
        </main>
      </div>
      <MicrositePreview
        isOpen={isPreviewOpen}
        onClose={() => setIsPreviewOpen(false)}
        title={previewContent.title}
        content={previewContent.content}
      />
    </div>
  )
}

