import React from 'react'
import { Terminal, Zap, GitBranch, FileText, ExternalLink } from 'lucide-react'
interface Resource {
  id: number
  title: string
  type: string
}
interface Node {
  id: number
  title: string
  timestamp: string
  iconType: string
  context: string
  description: string
  resources: Resource[]
  tags: string[]
}
interface NodeCardProps {
  node: Node
  onPreviewMicrosite: (title: string, content: React.ReactNode) => void
}
export function NodeCard({ node, onPreviewMicrosite }: NodeCardProps) {
  const getIcon = (iconType: string) => {
    switch (iconType) {
      case 'terminal':
        return <Terminal size={18} className="text-blue-400" />
      case 'zap':
        return <Zap size={18} className="text-yellow-400" />
      case 'branch':
        return <GitBranch size={18} className="text-green-400" />
      default:
        return <FileText size={18} className="text-gray-400" />
    }
  }
  const date = new Date(node.timestamp)
  const formattedDate = date.toLocaleDateString('en-US', {
    month: 'short',
    day: 'numeric',
    year: 'numeric',
  })
  const formattedTime = date.toLocaleTimeString('en-US', {
    hour: '2-digit',
    minute: '2-digit',
  })
  const previewContent = (
    <div className="p-4">
      <h3 className="text-xl font-bold mb-4">{node.title}</h3>
      <div className="mb-4">
        <h4 className="text-sm font-medium text-gray-400 mb-1">Context</h4>
        <p className="text-gray-200">{node.context}</p>
      </div>
      <div className="mb-4">
        <h4 className="text-sm font-medium text-gray-400 mb-1">Description</h4>
        <p className="text-gray-200">{node.description}</p>
      </div>
      <div>
        <h4 className="text-sm font-medium text-gray-400 mb-1">Resources</h4>
        <ul className="list-disc pl-5 text-gray-200">
          {node.resources.map((resource) => (
            <li key={resource.id}>{resource.title}</li>
          ))}
        </ul>
      </div>
    </div>
  )
  return (
    <div className="bg-gray-900 border border-gray-800 rounded-lg overflow-hidden">
      <div className="p-4">
        <div className="flex items-center justify-between mb-2">
          <div className="flex items-center">
            {getIcon(node.iconType)}
            <h3 className="ml-2 text-lg font-medium">{node.title}</h3>
          </div>
          <div className="text-xs text-gray-400">
            {formattedDate} • {formattedTime}
          </div>
        </div>
        <p className="text-sm text-gray-400 mb-3">{node.context}</p>
        <div className="flex flex-wrap gap-1 mb-3">
          {node.tags.map((tag) => (
            <span
              key={tag}
              className="text-xs px-2 py-1 bg-gray-800 text-gray-300 rounded-full"
            >
              #{tag}
            </span>
          ))}
        </div>
        <div className="flex justify-between items-center">
          <div className="text-xs text-gray-500">
            {node.resources.length} resources available
          </div>
          <button
            onClick={() => onPreviewMicrosite(node.title, previewContent)}
            className="flex items-center text-xs text-purple-400 hover:text-purple-300"
          >
            <ExternalLink size={14} className="mr-1" />
            View Details
          </button>
        </div>
      </div>
    </div>
  )
}

