Phase 2: Chat Gremlin (Day 1-2)
1. Basic Assistant Reply Observer
javascript// content.js - minimal Chat Gremlin
(() => {
  const chatContainer = document.querySelector('[data-testid="conversation-turn-list"]');
  if (!chatContainer) return;

  const observer = new MutationObserver(mutations => {
    for (const mutation of mutations) {
      for (const node of mutation.addedNodes) {
        if (!(node instanceof HTMLElement)) continue;

        const assistantMsg = node.querySelector('[data-testid="assistant-response"]');
        if (assistantMsg) {
          const text = assistantMsg.innerText.trim();
          console.log("🪱 Gremlin captured:", text.substring(0, 50) + "...");
          chrome.runtime.sendMessage({
            type: 'capture',
            content: text,
            timestamp: Date.now()
          });
        }
      }
    }
  });

  observer.observe(chatContainer, { childList: true, subtree: true });
  console.log("🪱 FloatMesh Gremlin active and watching...");
})();
2. Simple Background Broker
javascript// background.js - minimal message broker
chrome.runtime.onMessage.addListener((message, sender) => {
  if (message.type === 'capture') {
    // Store in local storage for history
    chrome.storage.local.get(['captureHistory'], result => {
      const history = result.captureHistory || [];
      history.push(message);
      chrome.storage.local.set({ captureHistory: history.slice(-20) }); // Keep last 20
    });

    // Log the capture
    console.log(`Captured from ${sender.tab.url}`);
  }
});
Phase 3: Obsidian Integration (Day 2-3)
1. Tag-Based Filter Rules
javascript// content.js - add to the gremlin logic
const filterRules = [
  {
    regex: /<obsidian:([^>]+)>/,
    destination: 'obsidian',
    process: (text, matches) => ({
      filepath: matches[1],
      content: text.replace(/<obsidian:[^>]+>/g, '').trim()
    })
  }
];

// When capturing text, check against filters
function processCapture(text) {
  for (const rule of filterRules) {
    if (rule.regex) {
      const matches = text.match(rule.regex);
      if (matches) {
        const processed = rule.process(text, matches);
        chrome.runtime.sendMessage({
          type: 'route',
          destination: rule.destination,
          data: processed,
          timestamp: Date.now()
        });
        return; // Stop after first match
      }
    }
  }

  // No matches, send as regular capture
  chrome.runtime.sendMessage({
    type: 'capture',
    content: text,
    timestamp: Date.now()
  });
}
2. Obsidian URI Handler
javascript// background.js - add to message listener
if (message.type === 'route' && message.destination === 'obsidian') {
  const vault = encodeURIComponent('YourVaultName');
  const filepath = encodeURIComponent(message.data.filepath);
  const content = encodeURIComponent(message.data.content);

  const uri = `obsidian://advanced-uri?vault=${vault}&filepath=${filepath}&mode=append&text=${content}`;

  chrome.tabs.create({ url: uri });
}
