2. Popup JavaScript
javascript// popup.js
document.addEventListener('DOMContentLoaded', () => {
  // Load capture history
  chrome.storage.local.get(['captureHistory'], result => {
    const history = result.captureHistory || [];
    const list = document.getElementById('captureList');

    if (history.length === 0) {
      list.innerHTML = '<li class="empty">No captures yet</li>';
      return;
    }

    list.innerHTML = '';
    history.forEach(item => {
      const li = document.createElement('li');
      const date = new Date(item.timestamp).toLocaleTimeString();
      li.innerHTML = `
        <span class="time">${date}</span>
        <p class="content">${item.content.substring(0, 100)}...</p>
      `;
      list.appendChild(li);
    });
  });

  // Button actions
  document.getElementById('captureBtn').addEventListener('click', () => {
    chrome.tabs.query({active: true, currentWindow: true}, tabs => {
      chrome.tabs.sendMessage(tabs[0].id, {type: 'triggerCapture'});
    });
  });

  document.getElementById('clearBtn').addEventListener('click', () => {
    chrome.storage.local.remove(['captureHistory'], () => {
      document.getElementById('captureList').innerHTML = 
        '<li class="empty">History cleared</li>';
    });
  });
});
